// jQuery Mask - Inicializa quando o DOM estiver pronto e também para elementos dinâmicos
jQuery(document).ready(function($) {
    // Função para aplicar máscara de moeda
    function applyMoneyMask() {
        if (typeof $.fn.mask !== 'undefined') {
            $('.money').each(function() {
                if (!$(this).data('mask-applied')) {
                    $(this).mask('#.##0,00', { reverse: true });
                    $(this).data('mask-applied', true);
                }
            });
        }
    }
    
    // Aguarda um pouco para garantir que o jQuery Mask está carregado (ele vem logo antes deste arquivo)
    setTimeout(function() {
        applyMoneyMask();
    }, 50);
    
    // Delegação para elementos adicionados dinamicamente (ex: modais, formulários carregados via AJAX)
    $(document).on('focus', '.money', function() {
        if (!$(this).data('mask-applied') && typeof $.fn.mask !== 'undefined') {
            $(this).mask('#.##0,00', { reverse: true });
            $(this).data('mask-applied', true);
        }
    });
});


const tooltipTriggerList = document.querySelectorAll('[data-bs-toggle="tooltip"]');
