// MODAL REMOTO
$('body').on('click', '[data-bs-toggle="modal-remote"]', function(){
	$(".modal-body").empty();
	var link = $(this).data("remote");
	var target = $(this).attr('data-bs-target');
	$(target+' .modal-body').load(link, function() {
		// Após carregar o conteúdo, atualizar contador de notificações
		if (typeof window.atualizarContadorNotificacoes === 'function') {
			setTimeout(function() {
				window.atualizarContadorNotificacoes();
			}, 100);
		}
	});
	$(target).modal('show');
});

$(document).on("hidden.bs.modal", ".modal:not(.local-modal)", function (e) {
	$(e.target).removeData("bs.modal").find(".modal-body").empty();
});

