// MODAL REMOTO
$(document).on('click', '[data-bs-remote="modal"]', function(e) {
	e.preventDefault();
	let modalTarget = $(this).data('bs-target');
	if (modalTarget) {
		let remoteContentUrl = $(this).data('remote');
		$(modalTarget).find('.modal-body').load(remoteContentUrl, function() {
			let modalInstance = new bootstrap.Modal($(modalTarget)[0]);
			modalInstance.show();
		});
	}
});

// Dark mode toogle
$('.darkmode').on('click', function () {
	let theme = '';
	theme = $(this).attr('data-theme');
	setCookie('themeMode', theme, 365);
	window.location.reload();
});

function setCookie(name,value,days) {
	var expires = "";
	if (days) {
		var date = new Date();
		date.setTime(date.getTime() + (days*24*60*60*1000));
		expires = "; expires=" + date.toUTCString();
	}
	eraseCookie(name);
	document.cookie = name + "=" + (value || "")  + expires + "; path=/";
}

function getCookie(name) {
	var nameEQ = name + "=";
	var ca = document.cookie.split(';');
	for(var i=0;i < ca.length;i++) {
		var c = ca[i];
		while (c.charAt(0)==' ') c = c.substring(1,c.length);
		if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length,c.length);
	}
	return null;
}

function eraseCookie(name) {   
	document.cookie = name+'=; Max-Age=-99999999;';  
}

// Função global para atualizar contador de notificações
window.atualizarContadorNotificacoes = function() {
	var domain = $('body').data('domain') || window.DOMAIN || '';
	if (!domain) {
		return;
	}
	
	$.ajax({
		url: domain + '/notificacoes/contar-nao-lidas',
		method: 'GET',
		dataType: 'json',
		success: function(data) {
			// Garantir que data seja um objeto
			if (typeof data === 'string') {
				try {
					data = JSON.parse(data);
				} catch(e) {
					return;
				}
			}
			
			if (data && data.success !== undefined) {
				var naoLidas = parseInt(data.nao_lidas) || 0;
				
				// Tentar múltiplos seletores para garantir que encontre o elemento
				var pulseCss = $('.notification_dropdown .pulse-css');
				if (pulseCss.length === 0) {
					pulseCss = $('.bell-link .pulse-css');
				}
				if (pulseCss.length === 0) {
					pulseCss = $('.pulse-css');
				}
				
				// Verificar se o elemento existe
				if (pulseCss.length === 0) {
					return;
				}
				
				// Atualizar o conteúdo do pulse-css
				if (naoLidas > 0) {
					// Se for maior que 9, mostrar "+9"
					var texto = naoLidas > 9 ? '+9' : naoLidas.toString();
					pulseCss.text(texto);
					
					// Ajustar tamanho se for "+9" para caber o texto
					if (naoLidas > 9) {
						pulseCss.css({
							'width': 'auto',
							'min-width': '1.2rem',
							'height': '1.2rem',
							'padding': '0 4px',
							'line-height': '1.2rem',
							'font-size': '10px',
							'font-weight': 'bold',
							'color': '#000',
							'display': 'block',
							'visibility': 'visible',
							'opacity': '1'
						});
					} else {
						// Restaurar tamanho padrão para números simples
						pulseCss.css({
							'width': '1.2rem',
							'height': '1.2rem',
							'padding': '0',
							'line-height': '1.2rem',
							'font-size': '12px',
							'font-weight': 'normal',
							'color': '#000',
							'display': 'block',
							'visibility': 'visible',
							'opacity': '1'
						});
					}
				} else {
					// Se não houver notificações, esconder
					pulseCss.text('').css({
						'display': 'none',
						'visibility': 'hidden'
					});
				}
			}
		},
		error: function(xhr, status, error) {
			// Erro silencioso
		}
	});
};

